import { Endpoint } from '../models/endpoint.model';
import { Scenario } from '../models/mock-definition/scenario/scenario.model';
import { MockDefinition } from '../models/mock-definition/mock-definition.model';
import { Store } from 'rxjs-observable-store';
import { Injectable } from '@angular/core';
import { OpenAPIV2 } from 'openapi-types';
import { VerbType } from '../models/verb-type';
import { Metadata } from '../models/mock-definition/metadata.model';
import { NGXLogger } from 'ngx-logger';
import { cloneDeep } from 'lodash';

export interface State {
  selectedEndpoint: Endpoint | null;
  selectedScenario: Scenario | null;
  mockDefinition: MockDefinition;
  mockDefinitions: Record<string, MockDefinition>;
  endpoints: Endpoint[];
}

Injectable();
@Injectable()
export class DesignerStore extends Store<State> {
  private static readonly mockDefinitionStoreKey = 'orbital_state_mockDefinition';
  private static readonly mockDefinitionsStoreKey = 'orbital_state_mockDefinitions';
  private static readonly endpointsStoreKey = 'orbital_state_endpoints';
  private static readonly selectedEndpointStoreKey = 'orbital_state_selectedEndpoint';
  private static readonly selectedScenarioStoreKey = 'orbital_state_selectedScenario';

  constructor(private logger: NGXLogger) {
    super({
      selectedEndpoint: JSON.parse(localStorage.getItem(DesignerStore.selectedEndpointStoreKey) ?? 'null'),
      selectedScenario: JSON.parse(localStorage.getItem(DesignerStore.selectedScenarioStoreKey) ?? 'null'),
      mockDefinition: JSON.parse(localStorage.getItem(DesignerStore.mockDefinitionStoreKey) ?? 'null'),
      mockDefinitions: JSON.parse(localStorage.getItem(DesignerStore.mockDefinitionsStoreKey) ?? 'null') || {},
      endpoints: JSON.parse(localStorage.getItem(DesignerStore.endpointsStoreKey) ?? 'null') || [],
    });

    this.state$.subscribe((state) => {
      const clonedDefinition = cloneDeep(state.mockDefinition);
      const clonedDefinitions = cloneDeep(state.mockDefinitions);
      localStorage.setItem(DesignerStore.mockDefinitionStoreKey, JSON.stringify(clonedDefinition));
      localStorage.setItem(DesignerStore.mockDefinitionsStoreKey, JSON.stringify(clonedDefinitions));
      localStorage.setItem(DesignerStore.endpointsStoreKey, JSON.stringify(state.endpoints));
      localStorage.setItem(DesignerStore.selectedEndpointStoreKey, JSON.stringify(state.selectedEndpoint));
      localStorage.setItem(DesignerStore.selectedScenarioStoreKey, JSON.stringify(state.selectedScenario));
    });
  }

  /**
   * This setter updates the Mockdefinitions map with the Mockdefinitions parameter
   * It creates a new Map for the store in order to trigger any render changes relying on this map.
   * Also updates the mockDefinition to the first mockDefinition in the list
   */
  set mockDefinitions(mockDefinitions: MockDefinition[]) {
    this.logger.debug('Setting mockDefinitions to ', mockDefinitions);
    this.setState({
      ...this.state,
      mockDefinitions: mockDefinitions.reduce<Record<string, MockDefinition>>((dictionary, mockDef) => {
        dictionary[mockDef.metadata.title] = mockDef;
        return dictionary;
      }, {}),
    });
    this.mockDefinition = mockDefinitions[0];
  }

  /**
   * Deletes a mock definition by title
   */
  deleteMockDefinitionByTitle(mockTitle: string) {
    if (this.state.mockDefinitions[mockTitle]) {
      this.logger.debug('Deleting mock ', mockTitle);
      const { [mockTitle]: _, ...mockDefinitions } = this.state.mockDefinitions;
      this.state.mockDefinitions = mockDefinitions;
      this.setState({ ...this.state });

      const mockDefNames = Object.keys(this.state.mockDefinitions ?? {});
      if (mockTitle === this.mockDefinition?.metadata?.title && mockDefNames.length) {
        this.logger.debug(
          'Mock store contains at least one mock; setting first mock to one in store ',
          this.state.mockDefinitions
        );
        this.mockDefinition = this.state.mockDefinitions[mockDefNames[0]];
        this.selectedEndpoint = null;
        this.selectedScenario = null;
      }
    }
  }

  /**
   * Updates a mock definition by title
   */
  private updateMockDefinitionsState(mockDefinition: MockDefinition) {
    this.logger.debug('Updating mock ', mockDefinition.metadata.title);
    this.state.mockDefinitions[mockDefinition.metadata.title] = mockDefinition;
    this.setState({ ...this.state });
  }

  /**
   * Appends a mock definition to the store; if one with the same name already exists
   * it will be overwritten
   */
  appendMockDefinition(mockDefinition: MockDefinition) {
    this.logger.debug('Appending mock definition', mockDefinition);
    this.updateMockDefinitionsState(mockDefinition);

    this.logger.debug('New state after appending', this.state);
    this.mockDefinition = Object.values(this.state.mockDefinitions)?.[0] ?? null;

    if (this.state.mockDefinition) {
      this.selectedEndpoint = null;
      this.selectedScenario = null;
    }
  }

  /**
   * This setter updates the selected endpoint for the designer store
   */
  set selectedEndpoint(endpoint: Endpoint | null) {
    this.logger.debug('setting selectedEndpoint to ', endpoint);
    this.setState({
      ...this.state,
      selectedEndpoint: endpoint ? { ...endpoint } : null,
    });
  }

  /**
   * This setter updates the selected scenario for the designer store
   */
  set selectedScenario(scenario: Scenario | null) {
    this.logger.debug('setting selectedScenario to ', scenario);
    this.setState({
      ...this.state,
      selectedScenario: scenario ? { ...scenario } : null,
    });
  }

  static extractOperationObject(
    pathItemObject: OpenAPIV2.PathItemObject,
    verb: VerbType
  ): OpenAPIV2.OperationObject | null {
    // eslint-disable-next-line @typescript-eslint/consistent-type-assertions
    const method = VerbType[verb] as keyof typeof OpenAPIV2.HttpMethods;
    return pathItemObject[OpenAPIV2.HttpMethods[method]] ?? null;
  }

  /**
   * setEndpoints reads the details of the endpoints specified in the Open Api document
   * and updates the state of the designer store.
   * @param doc The parsed Open Api document to extrapolate the endpoints from
   * @param clearStore Whether to clear the store before clearing the endpoints
   */
  setEndpoints(doc: OpenAPIV2.Document, clearStore = true): void {
    this.logger.debug('Clearing current endpoints: ', clearStore);
    const pathStrings = Object.keys(doc.paths);
    let endpoints: Endpoint[] = [];

    const httpMethods = Object.keys(OpenAPIV2.HttpMethods);
    const validVerbTypes: VerbType[] = Object.keys(VerbType)
      .map((v) => Number(v))
      .filter((v) => !isNaN(v) && httpMethods.includes(VerbType[v]));

    for (const path of pathStrings) {
      const pathObject: OpenAPIV2.PathItemObject = doc.paths[path];
      const newEndpoints: Endpoint[] = validVerbTypes
        .map((verb) => ({ path, verb, spec: DesignerStore.extractOperationObject(pathObject, verb) }))
        .filter((e): e is Endpoint => e.spec !== null);
      endpoints = [...endpoints, ...newEndpoints];
    }
    this.logger.debug('Endpoints from openApi document ', endpoints);
    this.setState({
      ...this.state,
      endpoints: clearStore ? [...endpoints] : [...this.state.endpoints, ...endpoints],
    });
  }

  /**
   * Setter method used to updated the MockDefinition in the designer store and the endpoints list
   * @param mockDefinition The MockDefinition used to update the store
   */
  set mockDefinition(mockDefinition: MockDefinition) {
    const mockDefinitionCopy = { ...mockDefinition };
    this.state.mockDefinitions[mockDefinition.metadata.title] = mockDefinitionCopy;
    this.logger.debug('Setting mockDefinition to ', mockDefinition);
    this.setState({ ...this.state, mockDefinition: mockDefinitionCopy });
    this.setEndpoints(mockDefinition.openApi);
  }

  /**
   * This method updates Metadata for the MockDefinition in the designer store
   *
   * @param metadata The metadata to update
   */
  updateMetadata(metadata: Metadata): void {
    this.logger.debug('Setting metadata to ', metadata);
    this.setState({
      ...this.state,
      mockDefinition: {
        ...this.state.mockDefinition,
        metadata: {
          ...metadata,
        },
      },
    });
  }

  /**
   * Add the provided scenario to current mock definition;
   * If scenario already exist, update the existing scenario
   * @param scenario Scenario to add to current mock definition
   */
  addOrUpdateScenario(scenario: Scenario) {
    const currentMock = this.state.mockDefinition;
    if (currentMock) {
      let current = currentMock.scenarios.find((s) => s.id === scenario.id);
      if (current) {
        this.logger.debug(
          'DesignerStore:AddOrUpdateScenario: Provided scenario already exist in the mock definition',
          current,
          scenario
        );
        current.metadata.title = scenario.metadata.title;
        current.metadata.description = scenario.metadata.description;

        current.requestMatchRules.bodyRules = scenario.requestMatchRules.bodyRules;
        current.requestMatchRules.headerRules = scenario.requestMatchRules.headerRules;
        current.requestMatchRules.queryRules = scenario.requestMatchRules.queryRules;
        current.requestMatchRules.urlRules = scenario.requestMatchRules.urlRules;
        current.defaultScenario = scenario.defaultScenario;

        current.policies = scenario.policies;

        current.response.body = scenario.response.body;
        current.response.headers = scenario.response.headers;
        current.response.status = scenario.response.status;
        current.response.type = scenario.response.type;

        current.tokenRule = scenario.tokenRule;
      } else {
        this.logger.debug(
          'DesignerStore:AddOrUpdateScenario: Unable to find provided scenario in the mock definition, Append it to the end of the list',
          scenario
        );
        current = scenario;
        currentMock.scenarios.push(current);
      }
      this.updateMockDefinitionsState(currentMock);
    }
  }

  /**
   * This method updates the host,basePath and openAPI spec of the MockDefinition in the designer store
   * @param host The string representing the host
   * @param basePath The string representing endpoint path
   * @param openApi The string representing openSpecAPI file contents
   */
  updateApiInformation(host: string, basePath: string, openApi: OpenAPIV2.Document): void {
    this.logger.debug('Setting host to ', host);
    this.logger.debug('Setting basePath to ', basePath);
    this.logger.debug('Setting openApi to ', openApi);
    this.setState({
      ...this.state,
      mockDefinition: {
        ...this.state.mockDefinition,
        host,
        basePath,
        openApi: { ...openApi },
      },
    });
  }

  /**
   * This method updates scenario array for the MockDefinition in the designer store
   * @param scenarios The list of scenarios to update
   */
  updateScenarios(scenarios: Scenario[]) {
    this.logger.debug('Setting scenarios to ', scenarios);
    this.mockDefinition = {
      ...this.state.mockDefinition,
      scenarios: [...scenarios],
    };
  }

  deleteScenario(scenarioId: string) {
    this.mockDefinition = {
      ...this.state.mockDefinition,
      scenarios: this.state.mockDefinition.scenarios.filter((s) => s.id !== scenarioId),
    };
  }
}

import { Injectable } from '@angular/core';
import { MockDefinition } from 'src/app/models/mock-definition/mock-definition.model';
import { Observable, throwError } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { NGXLogger } from 'ngx-logger';
import { cloneDeep } from 'lodash';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { DesignerStore } from '../../store/designer-store';

@Injectable({
  providedIn: 'root',
})
export class ExportMockdefinitionService {
  urlCache = '';
  mockdefinitionCache: MockDefinition | null = null;

  constructor(private store: DesignerStore, private httpClient: HttpClient, private logger: NGXLogger) {
    this.store.state$.subscribe((state) => {
      if (state.mockDefinition) {
        this.mockdefinitionCache = state.mockDefinition;
      }
    });
  }

  /**
   * Access to Url for Quick Export
   */
  getUrl(): string {
    return this.urlCache;
  }

  /**
   * Access to Mockdefinition for Quick Export
   */
  getMockdefinition(): MockDefinition | null {
    return this.mockdefinitionCache;
  }

  /**
   * POSTs a Mockdefinition to the server
   * @param url The url to post the mockdefinition to
   * @param mockdefinition The mockdefinition to be posted
   */
  exportMockDefinition(url: string, mockdefinition: MockDefinition): Observable<boolean> {
    this.logger.debug('Mockdefinition has been exported: ', mockdefinition);
    const mockDefinitionToExport = cloneDeep(mockdefinition);

    this.logger.debug('Mockdefinition in JSON format: ', mockDefinitionToExport);
    this.urlCache = url;
    this.mockdefinitionCache = mockDefinitionToExport;
    return this.httpClient
      .post<boolean>(url, mockDefinitionToExport, {
        headers: new HttpHeaders({
          'Content-Type': 'application/json; charset=utf-8',
        }),
      })
      .pipe(
        catchError((error) => {
          return throwError(error);
        })
      );
  }
}

import { Scenario, ScenarioParams } from 'src/app/models/mock-definition/scenario/scenario.model';
import { Injectable } from '@angular/core';
import { DesignerStore } from 'src/app/store/designer-store';
import { NGXLogger } from 'ngx-logger';
import { Observable } from 'rxjs';
import * as uuid from 'uuid';
import * as _ from 'lodash';
import { VerbType } from 'src/app/models/verb-type';
import { OpenAPIV2 } from 'openapi-types';
import { ResponseType } from 'src/app/models/mock-definition/scenario/response-type';
import { defaultTokenRule } from 'src/app/models/mock-definition/scenario/token-rule.model';
import * as HttpStatus from 'http-status-codes';
import { MockDefinition } from 'src/app/models/mock-definition/mock-definition.model';

@Injectable({
  providedIn: 'root',
})
export class MockDefinitionService {
  constructor(private store: DesignerStore, private logger: NGXLogger) {}

  /**
   * Parse provided string to mock definition, save it in the store and make it the current mockdefinition in the store.
   * @param mockDefinition String representation of mock definition
   */
  AddMockDefinitionToStore(mockDefinition: string): Observable<boolean> {
    return new Observable((observer) => {
      try {
        let content: MockDefinition = JSON.parse(mockDefinition);
        content = {
          ...content,
          scenarios: content.scenarios.map(
            (s: Scenario): Scenario => ({
              ...s,
              response: {
                ...s.response,
                headers: s.response.headers,
                type: s.response.type ?? ResponseType.CUSTOM,
              },
              requestMatchRules: {
                headerRules: s.requestMatchRules?.headerRules ?? [],
                queryRules: s.requestMatchRules?.queryRules ?? [],
                bodyRules: s.requestMatchRules?.bodyRules ?? [],
                urlRules: s.requestMatchRules?.urlRules ?? [],
              },
              policies: s.policies ?? [],
              tokenRule: {
                ...s.tokenRule,
                rules: s.tokenRule?.rules ?? [],
              },
              defaultScenario: s.defaultScenario ?? false,
            })
          ),
        };
        this.store.appendMockDefinition(content);
        this.store.mockDefinition = content;
        this.store.state.mockDefinition = content;
        observer.next(true);
      } catch (error) {
        observer.error(error);
      }
      observer.complete();
    });
  }

  /**
   * Validates if a string is a valid mockdefinition.
   * @param mockDefinition String representation of mock definition
   */
  validateMockDefinition(mockDefinition: string): Observable<boolean> {
    return new Observable((observer) => {
      try {
        JSON.parse(mockDefinition);
        observer.next(true);
      } catch (error) {
        observer.error(error);
      }
      observer.complete();
    });
  }

  /*
   * Clones the provided scenario and updates the mock definition in the store with the new copy scenario
   * @param mockId  string representation of mock definition's id
   * @param scenario Object representation of the scenario to be cloned
   */
  cloneScenario(mockId: string, scenario?: Scenario | null): Observable<boolean> {
    return new Observable((observer) => {
      try {
        if (!scenario || !scenario.id || !scenario.metadata || !scenario.metadata.title) {
          this.logger.warn('Scenario not cloned because it contains undefined attributes');
          observer.next(false);
          return;
        }

        // copy scenario using deep copy
        const clonedScenario = _.cloneDeep(scenario);
        clonedScenario.defaultScenario = false;
        clonedScenario.id = uuid.v4();
        clonedScenario.metadata.title = `${clonedScenario.metadata.title}-copy`;
        const scenarioMockDefinition = this.store.state.mockDefinitions[mockId];
        this.store.state.mockDefinition = scenarioMockDefinition;
        const originalScenarioIndex = scenarioMockDefinition.scenarios.indexOf(scenario);

        // ensure that there are no naming conflicts; if there are, repeat until a name is found
        if (scenarioMockDefinition.scenarios.find((x) => x.metadata.title === clonedScenario.metadata.title)) {
          let copyCounter = 2;
          while (
            scenarioMockDefinition.scenarios.find(
              (x) => x.metadata.title === `${clonedScenario.metadata.title} ${copyCounter}`
            )
          ) {
            copyCounter++;
          }

          clonedScenario.metadata.title = `${clonedScenario.metadata.title} ${copyCounter}`;
        }
        scenarioMockDefinition.scenarios.splice(originalScenarioIndex + 1, 0, clonedScenario);
        this.store.updateScenarios([...scenarioMockDefinition.scenarios]);
        this.logger.warn('Scenario successfully cloned: ', clonedScenario);
        observer.next(true);
      } catch (error) {
        observer.error(error);
      }
      observer.complete();
    });
  }

  /**
   * Generate default Scenarios based on the endpoints provided.
   *
   * @param endpoints list of endpoints from the imported openapi document
   */
  getDefaultScenarios(endpoints: OpenAPIV2.PathsObject, validation = false): Scenario[] {
    const defaultScenariosPerEndpoint: Scenario[] = [];
    const keyArrayOfEndpoints = Object.keys(endpoints);

    keyArrayOfEndpoints.forEach((pathName) => {
      const endpoint = endpoints[pathName];
      const types = this.getEndpointVerbTypes(endpoint);

      types.forEach((type) => {
        const newScenarioGet = this.generateNewScenario(this.defaultScenarioParams(pathName, type), true);
        defaultScenariosPerEndpoint.push(newScenarioGet);
        if (validation) {
          const tokenScenarioGet = this.generateNewScenario(
            this.defaultScenarioParams(pathName, type, HttpStatus.StatusCodes.UNAUTHORIZED, 'Invalid-Token Scenario')
          );
          defaultScenariosPerEndpoint.push(tokenScenarioGet);
        }
      });
    });
    return defaultScenariosPerEndpoint;
  }

  getDefaultValidationScenarios(scenarios: Scenario[]): Scenario[] {
    const scenarioList: Scenario[] = [];
    const scenarioDict = this.mapUnauthorizedScenarios(scenarios);

    for (const endpoint in scenarioDict) {
      for (const verb in scenarioDict[endpoint]) {
        if (!scenarioDict[endpoint][verb]) {
          const verbInt = parseInt(verb);
          scenarioList.push(
            this.generateNewScenario(
              this.defaultScenarioParams(
                endpoint,
                verbInt,
                HttpStatus.StatusCodes.UNAUTHORIZED,
                'Invalid-Token Scenario'
              )
            )
          );
        }
      }
    }
    return scenarioList;
  }

  private mapUnauthorizedScenarios(scenarios: Scenario[]): Record<string, Record<string, boolean>> {
    const scenarioDict: Record<string, Record<string, boolean>> = {};
    for (const scenario of scenarios) {
      let isUnauthorized: boolean = scenario.response.status === HttpStatus.StatusCodes.UNAUTHORIZED;

      if (scenarioDict[scenario.path]) {
        isUnauthorized = scenarioDict[scenario.path][scenario.verb] ? true : isUnauthorized;
      } else {
        scenarioDict[scenario.path] = {};
      }
      scenarioDict[scenario.path][scenario.verb] = isUnauthorized;
    }
    return scenarioDict;
  }

  defaultScenarioParams(
    path: string,
    verb: VerbType,
    status: HttpStatus.StatusCodes = HttpStatus.StatusCodes.OK,
    title: string = 'Default OK Scenario'
  ): ScenarioParams {
    return {
      title,
      description: '',
      path,
      status,
      verb,
    };
  }

  private getEndpointVerbTypes(endpoint: OpenAPIV2.PathItemObject): VerbType[] {
    const verbs: VerbType[] = [];
    const verbKeys = Object.keys(endpoint);
    verbKeys.forEach((key) => {
      // eslint-disable-next-line @typescript-eslint/consistent-type-assertions
      const type: VerbType | undefined = VerbType[key.toUpperCase() as keyof typeof VerbType];
      if (type !== undefined) {
        verbs.push(type);
      }
    });
    return verbs;
  }

  /**
   * Generates a new Scenario based on the path and verb.
   *
   */
  generateNewScenario(scenario: ScenarioParams, defaultScenario = false): Scenario {
    return {
      id: uuid.v4(),
      metadata: {
        title: scenario.title,
        description: scenario.description,
      },
      verb: scenario.verb,
      path: scenario.path,
      response: {
        headers: {},
        body: '{}',
        status: scenario.status,
        type: ResponseType.CUSTOM,
      },
      requestMatchRules: {
        headerRules: [],
        queryRules: [],
        bodyRules: [],
        urlRules: [],
      },
      policies: [],
      defaultScenario,
      tokenRule: defaultTokenRule,
    };
  }
}

