import { Pipe, PipeTransform } from '@angular/core';
import { Scenario } from '../../models/mock-definition/scenario/scenario.model';
import { Endpoint } from '../../models/endpoint.model';

@Pipe({
  name: 'getEndpointScenarios',
})
export class GetEndpointScenariosPipe implements PipeTransform {
  /**
   * Filters a list of scenarios, leaving only the scenarios whose path and verb match
   * the path and verb of the endpoint argument
   * @param scenarios The list of scenarios to filter
   * @param endpoint The endpoint to filter the scenarios against
   */
  transform(scenarios: Scenario[], endpoint: Endpoint): Scenario[] {
    if (!endpoint) {
      return scenarios;
    }
    return scenarios.filter(
      (scenario) =>
        scenario.path === endpoint.path &&
        scenario.verb.toString().toUpperCase() === endpoint.verb.toString().toUpperCase()
    );
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { RuleType } from 'src/app/models/mock-definition/scenario/rule.type';

@Pipe({
  name: 'getRuleTypeString',
})
export class GetRuleTypeStringPipe implements PipeTransform {
  /**
   * Takes in a ruleType and outputs the corresponding ruleType as a nicely formatted string
   * @param ruleType The ruleType to be piped in
   */
  transform(ruleType: RuleType): string {
    switch (+ruleType) {
      case 0:
        return 'None';
      case 1:
        return 'Regex';
      case 2:
        return 'Text: Starts With';
      case 3:
        return 'Text: Ends With';
      case 4:
        return 'Text: Contains';
      case 5:
        return 'Text: Equals';
      case 6:
        return 'JSON: Path';
      case 7:
        return 'JSON: Equality';
      case 8:
        return 'JSON: Contains';
      case 9:
        return 'JSON: Schema';
      default:
        return 'Invalid Rule';
    }
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { VerbType } from '../../models/verb.type';

@Pipe({
  name: 'getVerbColor',
})
export class GetVerbColorPipe implements PipeTransform {
  readonly blue: string = 'rgba(0, 163, 255, 0.25)';
  readonly green: string = 'rgba(30, 255, 160, 0.25)';
  readonly red: string = 'rgba(255, 0, 0, 0.25)';
  readonly yellow: string = 'rgba(250, 255, 0, 0.25)';
  readonly orange: string = 'rgba(255, 165, 0, 0.25)';
  readonly purple: string = 'rgba(82, 0, 255, 0.25)';
  readonly turquoise: string = 'rgba(64,224,208 ,1 )';
  readonly notsonavyblue: string = 'rgba(30,144,255 ,1)';

  /**
   * Returns a string containing the appropriate bootstrap color
   * class for the verb type. If a prefix string is passed into the parameters
   * then the pipe will prefix the color string with the prefix string.
   * @param verb The verb whose color we are trying to get
   * @param prefix An optional argument that prefixes the verbs
   * color string with the passed in string
   */
  transform(verb: VerbType, prefix = ''): string {
    switch (verb) {
      case VerbType.DELETE:
        return prefix + this.red;
      case VerbType.GET:
        return prefix + this.blue;
      case VerbType.POST:
        return prefix + this.green;
      case VerbType.PUT:
        return prefix + this.yellow;
      case VerbType.HEAD:
        return prefix + this.purple;
      case VerbType.OPTIONS:
        return prefix + this.notsonavyblue;
      case VerbType.PATCH:
        return prefix + this.turquoise;
      case VerbType.CONNECT:
      case VerbType.CUSTOM:
      case VerbType.NONE:
      case VerbType.TRACE:
        return prefix;
      default: {
        // Cause a type-check error if a case is missed
        const _: never = verb;
      }
    }
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { VerbType } from 'src/app/models/verb.type';

@Pipe({
  name: 'getVerbString',
})
export class GetVerbStringPipe implements PipeTransform {
  /**
   * Takes in a verbTypes and outputs the corresponding verb as a string
   * @param verb The verbType to be piped in
   */
  transform(verb: VerbType): string {
    switch (verb) {
      case VerbType.DELETE:
        return 'DELETE';
      case VerbType.GET:
        return 'GET';
      case VerbType.POST:
        return 'POST';
      case VerbType.PUT:
        return 'PUT';
      case VerbType.HEAD:
        return 'HEAD';
      case VerbType.OPTIONS:
        return 'OPTIONS';
      case VerbType.PATCH:
        return 'PATCH';
      case VerbType.CONNECT:
        return 'CONNECT';
      case VerbType.CUSTOM:
        return 'CUSTOM';
      case VerbType.NONE:
        return 'NONE';
      case VerbType.TRACE:
        return 'TRACE';
      default: {
        // Cause a type-check error if a case is missed
        const _: never = verb;
      }
    }
  }
}

